<template>  <div class="validationErrors">    <ul class="validationErrors__list">      <li class="validationErrors__item" v-for="errorMessage in errorMessages" :key="errorMessage">{{ errorMessage }}</li>    </ul>  </div></template><script>import './m_validationErrors.scss';export default {  name: "MvalidationErrors",  props: {    validationErrors: {      type: Object,      required: true,    },  },    computed: {      errorMessages() {        return Object.keys(this.validationErrors).map(name => {          const messages = this.validationErrors[name].join(', ')          return `${name} ${messages}`        })      },    },};</script>